import React, { useState, useEffect } from 'react';
import './App.css';

const ProductDisplay = ({ handleCheckout }) => (
  <section>
    <div className="product">
      <Logo />
      <div className="description">
        <h3>Starter plan</h3>
        <h5>$20.00 / month</h5>
      </div>
    </div>
    <button onClick={handleCheckout}>
      Checkout
    </button>
  </section>
);

const SuccessDisplay = ({ sessionId, handleManageBilling }) => (
  <section>
    <div className="product Box-root">
      <Logo />
      <div className="description Box-root">
        <h3>Subscription to starter plan successful!</h3>
      </div>
    </div>
    <button onClick={() => handleManageBilling(sessionId)}>
      Manage your billing information
    </button>
  </section>
);

const Message = ({ message }) => (
  <section>
    <p>{message}</p>
  </section>
);

export default function App() {
  let [message, setMessage] = useState('');
  let [success, setSuccess] = useState(false);
  let [sessionId, setSessionId] = useState('');

  useEffect(() => {
    const query = new URLSearchParams(window.location.search);

    if (query.get('success')) {
      setSuccess(true);
      setSessionId(query.get('session_id'));
    }

    if (query.get('canceled')) {
      setSuccess(false);
      setMessage(
        "Order canceled -- continue to shop around and checkout when you're ready."
      );
    }
  }, [sessionId]);

  const handleCheckout = async () => {
    try {
      const response = await fetch('/create-checkout-session', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          lookup_key: 'YOUR_PRODUCT_LOOKUP_KEY' // Replace with actual lookup key
        })
      });
      const data = await response.json();
      window.location.href = data.url;
    } catch (error) {
      console.error('Error creating checkout session:', error);
      setMessage('Error creating checkout session. Please try again later.');
    }
  };

  const handleManageBilling = async (sessionId) => {
    try {
      const response = await fetch('https://localhost:create-portal-session', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          session_id: sessionId
        })
      });
      const data = await response.json();
      window.location.href = data.url;
    } catch (error) {
      console.error('Error creating portal session:', error);
      setMessage('Error creating portal session. Please try again later.');
    }
  };

  if (!success && message === '') {
    return <ProductDisplay handleCheckout={handleCheckout} />;
  } else if (success && sessionId !== '') {
    return <SuccessDisplay sessionId={sessionId} handleManageBilling={handleManageBilling} />;
  } else {
    return <Message message={message} />;
  }
}

const Logo = () => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    xmlnsXlink="http://www.w3.org/1999/xlink"
    width="14px"
    height="16px"
    viewBox="0 0 14 16"
    version="1.1"
  >
    {/* SVG content */}
  </svg>
);
